// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './Login';
import Signup from './Signup';
import FeedScreen from './feed/FeedScreen';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal } from 'bootstrap';
import './Login.css';
import { authenticateUser } from './Users';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [userImg, setUserImg] = useState("");
  const [userList, setUserList] = useState([]);
  return (

  <Routes>
  {/* Uncomment and fix the routes as needed */}
  <Route path='/login' element={<Login setUsername={setUsername} setUserImg={setUserImg} userList={userList}/>} />
  <Route path='/' element={<Signup setUserList={setUserList}/>} />
  { loggedIn ? (
  <Route path='/feed' element={<FeedScreen username={username} userImg={userImg} />} />
  ) : (
  <Route path='/feed' element={<Navigate replace to={"/"}/>} />
  )}
  </Routes>

  );
  }


export default App;
