import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import Signup from './Signup';
import FeedScreen from './feed/FeedScreen';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal } from 'bootstrap';
import './Login.css';
import { authenticateUser } from './Users';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [userImg, setUserImg] = useState("");
  const [userList, setUserList] = useState([]);

  return (
    <Router>
      <Routes>
        {/* Route to login page */}
        <Route path='/login' element={<Login setUsername={setUsername} setUserImg={setUserImg} userList={userList} setLoggedIn={setLoggedIn} />} />

        {/* Route to signup page */}
        <Route path='/' element={<Signup setUserList={setUserList} />} />

        {/* Route to feed page - Conditionally render based on loggedIn state */}
        {loggedIn ? (
          <Route path='/feed' element={<FeedScreen username={username} userImg={userImg} />} />
        ) : (
          // Redirect to login page if not logged in
          <Route path='/feed' element={<Navigate replace to="/login" />} />
        )}
      </Routes>
    </Router>
  );
}

export default App;
