import { useState } from 'react';
import MainScreen from './MainScreen';
import './LightModeFeed.css';
import './NightModeFeed.css';

function FeedScreen({ displayName, profilePicture }) {
    const [mode, setMode] = useState(true);

    return (
        <>
            <title>Feed</title>
            <link
                rel="stylesheet"
                href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.1/dist/css/bootstrap.min.css"
                integrity="sha256-2TnSHycBDAm2wpZmgdi0z81kykGPJAkiUY+Wf97RbvY="
                crossOrigin="anonymous"
            />
            <div className={mode ? 'light-mode' : 'night-mode'}>
                <img src={URL.createObjectURL(profilePicture)} alt="Profile" />
                <MainScreen displayName={displayName} userImg="abc" mode={mode} setMode={setMode}></MainScreen>
            </div>
        </>
    );
}

export default FeedScreen;





